B=-A;
C = (0,1);
draw(A--B--C--A);
draw(C--(0,0),dashed);
O = (0,0);
label("$O$",O,S);
label("$A$",A,W);
label("$B$",B,E);
label("$C$",C,N);
draw(Circle(O,1));
[/asy]

