def solution():
    """A figure skater is facing north when she begins to spin to her right. She spins 2250 degrees. Which direction (north, south, east or west) is she facing when she finishes her spin?"""
    initial_direction = "north"
    degrees_spun = 2250
    final_direction = (initial_direction + degrees_spun) % 360

    if final_direction == 0:
        result = "north"
    elif final_direction == 90:
        result = "east"
    elif final_direction == 180:
        result = "south"
    elif final_direction == 270:
        result = "west"
    else:
        result = "unknown"

    return result

