import math

def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def solution():
    total = 0

    for num in range(100, 201):
        if math.isqrt(num) ** 2 == num - 1 or math.isqrt(num) ** 2 == num - 2:
            if is_prime(num):
                total += num

    return total

