def solution():
    """Let a and b be distinct positive divisors of 80. What is the smallest possible value of ab which is not a divisor of 80?"""
    n = 80
    divisors = []
    for i in range(1, int(n**0.5) + 1):
        if n % i == 0:
            divisors.append(i)
            if i != n // i:
                divisors.append(n // i)
    divisors.sort()
    smallest_ab = float('inf')
    for i in range(len(divisors)):
        for j in range(i+1, len(divisors)):
            ab = divisors[i] * divisors[j]
            if ab % n != 0 and ab < smallest_ab:
                smallest_ab = ab
    result = smallest_ab
    return result

