def solution():
    """What is the smallest two-digit prime that is the sum of three (not necessarily distinct) positive perfect cubes?"""
    smallest_prime = None
    num = 1

    while smallest_prime is None:
        sum_of_cubes = num ** 3 + num ** 3 + num ** 3

        if sum_of_cubes > 9 and sum_of_cubes < 100:
            is_prime = True

            for i in range(2, int(math.sqrt(sum_of_cubes)) + 1):
