def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def solution():
    """Find the sum of the first 9 prime numbers."""
    count = 0
    num = 2
    prime_sum = 0
    while count < 9:
        if is_prime(num):
            prime_sum += num
            count += 1
        num += 1
    result = prime_sum
    return result

